<?php/** * 主题相关参数设置 * * @since 2021-10-27 * @version 0.1027 * @package functions.php * @author zitonglu/910109610@qq.com * @copyright rujue.com.cn */function rujuebook_ad_theme_setting(){  if($_POST['rujuebook_update_themeoptions']=='true') {rujuebook_themeoptions_update();}  add_theme_page(__('网站设置','rujuebook'),__('网站设置','rujuebook'),'administrator','theme_setting','rujuebook_theme_setting'); }   add_action('admin_menu','rujuebook_ad_theme_setting');//设置关键词function rujuebook_themeoptions_update(){  update_option('rujuebook_homeKeyword',$_POST['homeKeyword']);  update_option('rujuebook_homeDescription',$_POST['homeDescription']);  update_option('rujuebook_top_JQ',stripslashes($_POST['top_JQ']));  update_option('rujuebook_bottom_JQ',stripslashes($_POST['bottom_JQ']));  update_option('CDN_array_url',$_POST['CDN_array_url']);  update_option('header_logo_image',$_POST['logo_url']);}function rujuebook_theme_setting(){ ?><div class="wrap">  <h1><?php _e('网站设置','rujuebook')?></h1>  <hr>  <form method="POST" action="" style="padding-left:20px;">  <input type="hidden" name="rujuebook_update_themeoptions" value="true" /><!-- hidden -->  <table class="form-table" role="presentation">    <tbody>      <tr>        <th scope="row"><label for="homeKeyword"><?php _e('首页关键词','rujuebook')?></label></th>        <td>          <input name="homeKeyword" type="text" id="homeKeyword" value="<?php echo get_option('rujuebook_homeKeyword'); ?>" class="regular-text">          <p class="description"><?php _e('多个词请使用英文半角逗号隔开','rujuebook')?></p>        </td>      </tr><!-- 首页关键词 end -->      <tr>        <th scope="row"><label for="homeDescription"><?php _e('首页描述','rujuebook')?></label></th>        <td>          <textarea type="text" name="homeDescription" cols="60" rows="3"><?php echo get_option('rujuebook_homeDescription'); ?></textarea>        </td>      </tr><!-- 首页描述 end -->      <tr>        <th scope="row"><label for="top_JQ"><?php _e('顶部JS/JQ','rujuebook')?></label></th>        <td>          <textarea name="top_JQ" cols="60" rows="4"><?php echo get_option('rujuebook_top_JQ'); ?></textarea>          <p class="description"><?php _e('网站顶部的JS/JQ代码','rujuebook')?></p>        </td>      </tr><!-- 顶部JS/JQ end -->      <tr>        <th scope="row"><label for="bottom_JQ"><?php _e('底部JS/JQ','rujuebook')?></label></th>        <td>          <textarea name="bottom_JQ" cols="60" rows="4"><?php echo get_option('rujuebook_bottom_JQ'); ?></textarea>          <p class="description"><?php _e('网站底部的JS/JQ代码','rujuebook')?></p>        </td>      </tr><!-- 底部JS/JQ end -->      <tr>        <th scope="row"><label for="CDN_array_url">CDN<?php _e('加速网址','dianzai')?></label></th>        <td>          <textarea name="CDN_array_url" cols="100" rows="10"><?php echo get_option('CDN_array_url'); ?></textarea>          <p class="description"><?php _e('输入需要加速的JS后者css文件，一行一个加速网址，相应位置插入类似代码','dianzai')?>:</p>          <p><code>&lt;?php _echo_CDN_URL('bootstrap.min.js')?&gt;<br>&lt;?php _echo_CDN_URL('bootstrap.min.css','css')?&gt;</code></p>        </td>      </tr><!-- CDN加速网址 end -->      <tr>        <th scope="row"><label for="logo_url"><?php _e('网站LOGO','rujuebook')?></label></th>        <td>          <input name="logo_url" type="url" id="logo_url" value="<?php echo get_option('header_logo_image'); ?>" class="regular-text">          <p class="description"><?php _e('网站LOGO的URL','rujuebook')?></p>        </td>      </tr><!-- 网站LOGO end -->    </tbody>  </table>    <p><input type="submit" name="submit" id="submit" class="button button-primary" value="<?php _e('提交修改','rujuebook') ?>">    </p>  </form></div><?php }?>